select 	EMP_TAX_DISCOUNT_ID,	CODE_DISC,	date_start_disc,    date_end_disc, 	DECODE(custom_sign,0, STAND_SUM_DISC, etd.sum_discount) as sum_discount from {1}.emp_tax_discount etd 	join {1}.type_discount td using (type_discount_id) 	join {0}.TRANSFER using (transfer_id)where (per_num, NVL(sign_comb,0)) in 	(select per_num, NVL(sign_comb,0) from {0}.transfer where  transfer_id=:p_transfer_id)	and date_start_disc<add_months(trunc(:p_date,'year'),12) 	and date_end_disc>=trunc(:p_date,'year')